import { Dialog, DialogTitle, DialogContent, Typography, Chip, Button } from '@mui/material';

export default function CharacterDetail({ character, open, onClose }) {
  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>{character?.name}</DialogTitle>
      <DialogContent>
        <img
          src={character?.imageUrl || 'https://via.placeholder.com/300'}
          alt={character?.name}
          style={{ width: '100%', marginBottom: 16 }}
        />
        <Chip
          label={character?.rarity}
          color={
            character?.rarity === 'Common' ? 'default' :
              character?.rarity === 'Rare' ? 'primary' : 'secondary'
          }
          sx={{ mb: 2 }}
        />
        <Typography variant="body1" paragraph>
          {character?.description || 'No description available'}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Stars: {'★'.repeat(character?.upgradeLevels || 1)}
        </Typography>
        <Button
          onClick={onClose}
          variant="outlined"
          sx={{ mt: 3 }}
        >
          Close
        </Button>
      </DialogContent>
    </Dialog>
  );
}
