import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  TextField,
  InputAdornment,
  Container,
  Grid,
  Card,
  CardMedia,
  CardContent,
  Typography,
  Chip
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';


function CharactersList() {
  const [searchTerm, setSearchTerm] = useState('');
  const [characters, setCharacters] = useState([]);

  const fetchCharacters = async (term = '') => {
    const response = await axios.get(`http://localhost:5062/api/GameCharacters/search?searchTerm=${term}`);
    setCharacters(response.data);
  };

  useEffect(() => { fetchCharacters(); }, []);

  return (
    <div>
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Search characters..."
          value={searchTerm}
          onChange={(e) => {
            setSearchTerm(e.target.value);
            fetchCharacters(e.target.value);
          }}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon />
              </InputAdornment>
            ),
          }}
          sx={{ mb: 4 }}
        />

        <div style={{ padding: '20px' }}>
          <h1>Game Characters</h1>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px' }}>
            {characters.map(character => (
              <div key={character.id} style={{ border: '1px solid #ddd', padding: '10px', borderRadius: '5px' }}>
                <img
                  src={character.imageUrl || 'https://via.placeholder.com/150'}
                  alt={character.name}
                  style={{ width: '100%', height: '300px', objectFit: 'cover' }}
                />
                <h3>{character.name}</h3>
                <p>Rarity: {character.rarity}</p>
                <p>Stars: {'★'.repeat(character.upgradeLevels)}</p>
              </div>
            ))}
          </div>
        </div>
      </Container>
    </div>
  );
}

export default CharactersList;
