import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CharactersList() {
  const [searchTerm, setSearchTerm] = useState('');
  const [characters, setCharacters] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5062/api/gamecharacters')
      .then(response => setCharacters(response.data))
      .catch(error => console.error('Error fetching data:', error));

    const fetchCharacters = async (term = '') => {
      const response = await axios.get(`http://localhost:5000/api/gamecharacters?searchTerm=${term}`);
      setCharacters(response.data);
    };
  }, []);
  return (
    <div>
      <input
        type="text"
        placeholder="Search characters..."
        value={searchTerm}
        onChange={(e) => {
          setSearchTerm(e.target.value);
          fetchCharacters(e.target.value);
        }}
      />
      <div style={{ padding: '20px' }}>
        <h1>Game Characters</h1>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px' }}>
          {characters.map(character => (
            <div key={character.id} style={{ border: '1px solid #ddd', padding: '10px', borderRadius: '5px' }}>
              <img
                src={character.imageUrl || 'https://via.placeholder.com/150'}
                alt={character.name}
                style={{ width: '100%', height: '150px', objectFit: 'cover' }}
              />
              <h3>{character.name}</h3>
              <p>Rarity: {character.rarity}</p>
              <p>Stars: {'★'.repeat(character.upgradeLevels)}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default CharactersList;
