import {
  Dialog, DialogTitle, DialogContent,
  Typography, Chip, Button, Stack, IconButton
} from '@mui/material';
import { Edit, Delete } from '@mui/icons-material';
import { useState } from 'react';

export default function CharacterDetail({ character, open, onClose, userRole }) {
  const [confirmOpen, setConfirmOpen] = useState(false);

  const handleDeleteClick = () => setConfirmOpen(true);
  const handleDeleteConfirm = () => {
    console.log('Delete character:', character.id); // Temporary
    setConfirmOpen(false);
    onClose();
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>{character?.name}{userRole && ['Admin', 'Writer'].includes(userRole) && (
        <Stack direction="row" spacing={1} sx={{ float: 'right' }}>
          <IconButton color="primary" sx={{
            '&:hover': {
              backgroundColor: 'primary.light',
              transform: 'scale(1.1)'
            }
          }}>
            <Edit />
          </IconButton>
          <IconButton color="error" sx={{
            '&:hover': {
              backgroundColor: 'error.light',
              transform: 'scale(1.1)'
            }
          }} onClick={handleDeleteClick}>
            <Delete />
          </IconButton>
        </Stack>
      )}
      </DialogTitle>
      <DialogContent>
        <img
          src={character?.imageUrl || 'https://via.placeholder.com/300'}
          alt={character?.name}
          style={{ width: '100%', marginBottom: 16 }}
        />
        <Chip
          label={character?.rarity}
          color={
            character?.rarity === 'Common' ? 'default' :
              character?.rarity === 'Rare' ? 'primary' : 'secondary'
          }
          sx={{ mb: 2 }}
        />
        <Typography variant="body1" paragraph>
          {character?.description || 'No description available'}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Stars: {'★'.repeat(character?.upgradeLevels || 1)}
        </Typography>
        <Button
          onClick={onClose}
          variant="outlined"
          sx={{ mt: 3 }}
        >
          Close
        </Button>
      </DialogContent>
    </Dialog>


  );
}
