import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  TextField,
  InputAdornment,
  Container,
  Grid,
  Card,
  CardMedia,
  CardContent,
  Typography,
  Chip
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import { Box } from '@mui/material';
import CharacterDetail from './CharacterDetail';


function CharactersList() {
  const [searchTerm, setSearchTerm] = useState('');
  const [characters, setCharacters] = useState([]);
  const [selectedCharacter, setSelectedCharacter] = useState(null);

  const fetchCharacters = async (term = '') => {
    const response = await axios.get(`http://localhost:5062/api/GameCharacters/search?searchTerm=${term}`);
    setCharacters(response.data);
  };

  useEffect(() => { fetchCharacters(); }, []);

  return (
    <div>
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Search characters..."
          value={searchTerm}
          onChange={(e) => {
            setSearchTerm(e.target.value);
            fetchCharacters(e.target.value);
          }}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon />
              </InputAdornment>
            ),
          }}
          sx={{ mb: 4 }}
        />

        <Grid container spacing={3}>
          <div style={{ padding: '20px' }}>
            <h1>Game Characters</h1>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px' }}>
              {characters.map(character => (
                <Card sx={{ border: '1px solid #ddd', padding: '10px', borderRadius: '5px' }}>
                  <CardMedia
                    component="img"
                    height="200"
                    image={character.imageUrl || 'https://via.placeholder.com/150'}
                    alt={character.name}
                    style={{ width: '100%', height: '300px', objectFit: 'cover' }}
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div" sx={{ mb: 1 }}>
                      {character.name}
                    </Typography>
                    <Chip
                      label={character.rarity}
                      size="small"
                      color={
                        character.rarity === 'Common' ? 'default' :
                          character.rarity === 'Rare' ? 'primary' : 'secondary'
                      }
                      sx={{ mb: 1 }}
                    />
                    <Typography variant="body2" color="text.secondary">
                      {character.description}
                    </Typography>
                    <Box sx={{ mt: 1 }}>
                      {[...Array(character.upgradeLevels)].map((_, i) => (
                        <span key={i}>★</span>
                      ))}
                    </Box>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>
        </Grid>
      </Container>
    </div>
  );
}

export default CharactersList;
