import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CharacterDetail from './CharacterDetail';
import './CharactersList.css'; // We'll create this CSS file

function CharactersList() {
  const [searchTerm, setSearchTerm] = useState('');
  const [characters, setCharacters] = useState([]);
  const [selectedCharacter, setSelectedCharacter] = useState(null);

  const fetchCharacters = async (term = '') => {
    try {
      const response = await axios.get(
        `http://localhost:5062/api/GameCharacters/search?searchTerm=${term}`
      );
      setCharacters(response.data);
    } catch (error) {
      console.error('Error fetching characters:', error);
    }
  };

  const handleCharacterUpdated = (updatedCharacter) => {
    setCharacters(prev => prev.map(c =>
      c.id === updatedCharacter.id ? updatedCharacter : c
    ));
  };

  useEffect(() => {
    fetchCharacters();
  }, []);

  return (
    <div className="characters-container">
      <div className="search-container">
        <div className="search-box">
          <span className="search-icon">üîç</span>
          <input
            type="text"
            placeholder="Search characters..."
            value={searchTerm}
            onChange={(e) => {
              setSearchTerm(e.target.value);
              fetchCharacters(e.target.value);
            }}
          />
        </div>
      </div>

      <h1 className="page-title">Game Characters</h1>

      <div className="characters-grid">
        {characters.map(character => (
          <div
            key={character.id}
            className="character-card"
            onClick={() => setSelectedCharacter(character)}
          >
            <img
              src={character.imageUrl || 'https://via.placeholder.com/300'}
              alt={character.name}
              className="character-image"
            />
            <div className="character-content">
              <h3>{character.name}</h3>
              <span className={`rarity-badge ${character.rarity.toLowerCase()}`}>
                {character.rarity}
              </span>
              <p className="description">
                {character.description || 'No description available'}
              </p>
              <div className="stars">
                {'‚òÖ'.repeat(character.upgradeLevels)}
              </div>
            </div>
          </div>
        ))}
      </div>

      <CharacterDetail
        character={selectedCharacter}
        open={Boolean(selectedCharacter)}
        onClose={() => setSelectedCharacter(null)}
        userRole="Writer"
        onCharacterUpdated={handleCharacterUpdated}
      />
    </div>
  );
}

export default CharactersList;
