import {
  Dialog, DialogTitle, DialogContent,
  Typography, Chip, Button, Stack, IconButton, DialogActions, TextField, Box
} from '@mui/material';
import { Edit, Delete } from '@mui/icons-material';
import { useState } from 'react';
import axios from 'axios';

export default function CharacterDetail({ character, open, onClose, userRole, onCharacterUpdated }) {
  const [confirmOpen, setConfirmOpen] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    name: character?.name || '',
    description: character?.description || '',
    rarity: character?.rarity || 'Common',
    upgradeLevels: character?.upgradeLevels || 1,
    imageUrl: character?.imageUrl || ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    try {
      const response = await axios.put(
        `http://localhost:5000/api/gamecharacters/${character.id}`,
        formData,
        {
          headers: { 'Content-Type': 'application/json' }
        }
      );
      onCharacterUpdated(response.data); // Update parent
      setEditMode(false);
    } catch (error) {
      console.error('Update failed:', error);
      // Add error notification here if needed
    }
  };
  const handleDeleteClick = () => setConfirmOpen(true);
  const handleDeleteConfirm = () => {
    console.log('Delete character:', character.id); // Temporary
    setConfirmOpen(false);
    onClose();
  };

  return (
    <>
      <Dialog open={open} onClose={onClose}>
        <DialogTitle>
          {editMode ? 'Edit Character' : character?.name}
          {!editMode && userRole && ['Admin', 'Writer'].includes(userRole) && (
            <Stack direction="row" spacing={1} sx={{ float: 'right' }}>
              <IconButton color="primary" onClick={() => setEditMode(true)}>
                <Edit />
              </IconButton>
              <IconButton color="error" sx={{
                '&:hover': {
                  backgroundColor: 'error.light',
                  transform: 'scale(1.1)'
                }
              }} onClick={handleDeleteClick}>
                <Delete />
              </IconButton>
            </Stack>
          )}
        </DialogTitle>

        <DialogContent>
          {editMode ? (
            <Box component="form" sx={{ mt: 1 }}>
              <TextField
                fullWidth
                margin="normal"
                label="Name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
              />
              <TextField
                fullWidth
                multiline
                rows={3}
                margin="normal"
                label="Description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
              />
              <TextField
                select
                fullWidth
                margin="normal"
                label="Rarity"
                name="rarity"
                value={formData.rarity}
                onChange={handleInputChange}
              >
                <option value="Common">Common</option>
                <option value="Rare">Rare</option>
                <option value="Epic">Epic</option>
              </TextField>
              <TextField
                type="number"
                fullWidth
                margin="normal"
                label="Upgrade Levels (1-5)"
                name="upgradeLevels"
                value={formData.upgradeLevels}
                onChange={handleInputChange}
                inputProps={{ min: 1, max: 5 }}
              />
              <TextField
                fullWidth
                margin="normal"
                label="Image URL"
                name="imageUrl"
                value={formData.imageUrl}
                onChange={handleInputChange}
              />
            </Box>
          ) : (
            <>
              <img
                src={character?.imageUrl || 'https://via.placeholder.com/300'}
                alt={character?.name}
                style={{ width: '100%', marginBottom: 16 }}
              />
              <Chip
                label={character?.rarity}
                color={
                  character?.rarity === 'Common' ? 'default' :
                    character?.rarity === 'Rare' ? 'primary' : 'secondary'
                }
                sx={{ mb: 2 }}
              />
              <Typography variant="body1" >
                {character?.description || 'No description available'}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Stars: {'â˜…'.repeat(character?.upgradeLevels || 1)}
              </Typography>
            </>
          )}
        </DialogContent>

        {editMode && (
          <DialogActions>
            <Button onClick={() => setEditMode(false)}>Cancel</Button>
            <Button onClick={handleSave} color="primary" variant="contained">
              Save Changes
            </Button>
          </DialogActions>
        )}
      </Dialog>

      <Dialog open={confirmOpen} onClose={() => setConfirmOpen(false)}>
        <DialogTitle>Delete Character?</DialogTitle>
        <DialogContent>
          <Typography>Are you sure you want to delete "{character?.name}"?</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setConfirmOpen(false)}>Cancel</Button>
          <Button
            onClick={handleDeleteConfirm}
            color="error"
            variant="contained"
          >
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
}
